    private String getValue( String key ) throws Exception {
        String value = "" ;
        try {
            value = (String) dataMap.get( key ) ;
            if ( value == null )
                throw new Exception( key + " : Key not found" ) ;
        }
		catch ( Exception exception ) {
            throw new Exception( key + " : Key not found."+exception) ;
        }
        return value ;
    }

